// client/src/App.js

import React from "react";
import logo from "./logo.svg";
import "./App.css";

function Item(props) {
  const [checked, setChecked] = React.useState(false);
  function check() {
    setChecked(!checked);
    if (checked) {
      fetch("/inclusion-done", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({inclusion_name: props.text})
      }).then(() => {
        setTimeout(() => {setChecked(false)}, 500);
      });
    }

  }
  return <div>
      <p>{props.text}</p><input type="checkbox" checked={checked} onChange={check}/>
    </div>
}

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/get-data")
        .then((res) => res.json())
        .then((data) => setData(data.inclusions));
  }, []);
  if (!data) {
    return <div>Loading...</div>;
  }
  console.log(data);

  return (
    <div className="App">
      {data.map(inclusion => 
        <Item 
        key={inclusion}
        text={inclusion}/>
      )}
    </div>
  );
}

export default App;
