// client/src/App.js

import React from "react";
import logo from "./logo.svg";
import "./App.css";

function Item(props) {
  return <div>
      <textarea value={props.text}/>
    </div>
}

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    if (!data) {
      fetch("/get-data")
        .then((res) => res.json())
        .then((data) => setData(data.inclusions));
    }
    if (data) {
      fetch("/post-data", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ inclusions: data }),
      });
    }
  }, [data]);
  if (!data) {
    return <div>Loading...</div>;
  }
  return (
    <div className="App">
      {data.map(item => 
        <Item key={item} text={item}/>
      )}
    </div>
  );
}

export default App;
