// client/src/App.js

import React from "react";
import logo from "./logo.svg";
import "./App.css";

function Item(props) {
  return <div>
      <p>{props.text}</p><input type="checkbox"/>
    </div>
}

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    if (data) {
      fetch("/post-data", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ inclusions: data }),
      });
    }
    fetch("/get-data")
        .then((res) => res.json())
        .then((data) => setData(data.inclusions));
  }, [data]);
  React.useEffect(() => {
    setData(["one", "two", "three", "four"]);
  }, []);
  if (!data) {
    return <div>Loading...</div>;
  }
  return (
    <div className="App">
      {data.map(inclusion => 
        <Item 
        key={inclusion}
        text={inclusion}/>
      )}
    </div>
  );
}

export default App;
